package main

import (
	"fmt"
	"strings"
)

func main() {
	s := "abacbabc"
	p := "abc"
	// s := "cbaebabacd"
	// p := "abc"
	// s := "abab"
	// p := "ab"
	// s := ""
	// p := ""
	fmt.Printf("Output: %v\n", findAnagrams(s, p))
}

// 执行用时 : 316 ms, 在Find All Anagrams in a String的Go提交中击败了58.67% 的用户
// 内存消耗 : 8.5 MB, 在Find All Anagrams in a String的Go提交中击败了76.47% 的用户
func findAnagrams(s string, p string) []int {
	var substr string
	var isAna bool
	letterMap := map[string]int{"a": 0, "b": 0, "c": 0, "d": 0, "e": 0, "f": 0, "g": 0, "h": 0,
		"i": 0, "j": 0, "k": 0, "l": 0, "m": 0, "n": 0, "o": 0, "p": 0, "q": 0, "r": 0, "s": 0, "t": 0,
		"u": 0, "v": 0, "w": 0, "x": 0, "y": 0, "z": 0}
	for i := 0; i < len(p); i++ {
		letterMap[p[i:i+1]] = strings.Count(p, p[i:i+1])
	}
	indexs := make([]int, 0)
	for i := 0; i < len(s)-len(p)+1; i++ {
		if isAna {
			substr = s[i+len(p)-1 : i+len(p)]
			if letterMap[substr] == 0 || strings.Count(p, substr) != letterMap[substr] ||
				strings.Count(s[i:i+len(p)], substr) != letterMap[substr] {
				isAna = false
			}
		} else {
			substr = s[i : i+len(p)]
			isAna = true
			if substr != p {
				for k, v := range letterMap {
					if strings.Count(substr, k) != v {
						isAna = false
						break
					}
				}
			}
		}
		if isAna {
			indexs = append(indexs, i)
		}
	}
	return indexs
}
